 			 }.show()
 			 }
 		}
 }

import android.os.Bundle
 import android.view.Gravity
 import android.widget.Toast
 import androidx.appcompat.app.AppCompatActivity
 import kotlinx.android.synthetic.main.activity_main.* 


class MainActivity : AppCompatActivity()
 {
 		override fun onCreate(savedInstanceState: Bundle?) 
 		 {
 		super.onCreate(savedInstanceState) 
 	 	 setContentView(R.layout.activity_main) 


	 	 button1.setOnClickListener {
 	 	 val toast = Toast.makeText(this@MainActivity, "CENTER", Toast.LENGTH_SHORT) 
 		 toast.setGravity(Gravity.CENTER, 0, 0) 
 		 toast.show()
 	 	 }


	 	 button2.setOnClickListener {
 	 	 val toast = Toast.makeText(this@MainActivity, " TOP", Toast.LENGTH_SHORT) 
 	 	 toast.setGravity(Gravity.TOP, 0, 250) 
 	 	 toast.show()
 	 	 }


	 	 button3.setOnClickListener {
 	 	 val toast = Toast.makeText(this@MainActivity, "BOTTOM", Toast.LENGTH_SHORT) 
 	 	 toast.setGravity(Gravity.BOTTOM, 0, 250) 
 		 toast.show()
 	 	 }


	 	 button4.setOnClickListener {
 	 	 val toast = Toast.makeText(this@MainActivity, "LEFT", Toast.LENGTH_SHORT) 
 	 	 toast.setGravity(Gravity.LEFT, 0, 250) 
 	 	 toast.show()
 	 	 }


	 	 button5.setOnClickListener {
 	 	 val toast = Toast.makeText(this@MainActivity, "RIGHT", Toast.LENGTH_SHORT) 
 	 	 toast.setGravity(Gravity.RIGHT, 0, 250) 
 	 	 toast.show()
 	 	 }	}
 }

import android.os.Bundle
 import android.view.View
 import android.widget.TextView
 import android.widget.Toast
 import androidx.appcompat.app.AppCompatActivity


class MainActivity : AppCompatActivity()
 {
 override fun onCreate(savedInstanceState: Bundle?) 
 		{
 			super.onCreate(savedInstanceState) 
 			 setContentView(R.layout.activity_main) 


			 Toast.makeText(applicationContext, ""onCreate invoked"", Toast.LENGTH_SHORT).show()

 		}


override fun onStart() {
 			 super.onStart()
 			 Toast.makeText(applicationContext, ""onStart invoked"", Toast.LENGTH_SHORT).show()
 }


override fun onResume() {
 			 super.onResume()
 			 Toast.makeText(applicationContext, ""onResume invoked"", Toast.LENGTH_SHORT).show()
 }


override fun onPause() {
 			 super.onPause()
 			 Toast.makeText(applicationContext, ""onPause invoked"", Toast.LENGTH_SHORT).show()
 }


override fun onStop() {
 			 super.onStop()
 			 Toast.makeText(applicationContext, ""onStop invoked"", Toast.LENGTH_SHORT).show()
 }


override fun onRestart() {
 			 super.onRestart()
 			 Toast.makeText(applicationContext, ""onRestart invoked"", Toast.LENGTH_SHORT).show()
 }


override fun onDestroy() {
 			 super.onDestroy()
 			 Toast.makeText(applicationContext, ""onDestroy invoked"", Toast.LENGTH_SHORT).show()
 }

 }

