 import android.os.Bundle
 import kotlinx.android.synthetic.main.activity_main.* 


class MainActivity : AppCompatActivity() {


	override fun onCreate(savedInstanceState: Bundle?) {
 		super.onCreate(savedInstanceState) 
 		setContentView(R.layout.activity_main) 


		btn.setOnClickListener {

			var str:String = et.text.toString()


			val intent = Intent(applicationContext,Main2Activity::class.java) 
 			intent.putExtra(""msg"",str); 
 			startActivity(intent) 
 			}
 		}

 }

import android.content.Intent
 import androidx.appcompat.app.AppCompatActivity
 import android.os.Bundle
 import kotlinx.android.synthetic.main.activity_main.* 


class MainActivity : AppCompatActivity() {


	override fun onCreate(savedInstanceState: Bundle?) {
 		super.onCreate(savedInstanceState) 
 		setContentView(R.layout.activity2_main) 


			 val bundle:Bundle? = intent.extras 

			 val str = bundle!!.getString(""msg"") 

			 tv.text = str 

		}

 }

import android.content.DialogInterface import android.os.Bundle
 import android.widget.Toast
 import androidx.appcompat.app.AlertDialog
 import androidx.appcompat.app.AppCompatActivity
 import kotlinx.android.synthetic.main.activity_main.*


class MainActivity : AppCompatActivity()
 {


	override fun onCreate(savedInstanceState: Bundle?)
 	{
 		super.onCreate(savedInstanceState)
 		setContentView(R.layout.activity_main)


		btn_alert.setOnClickListener {
 			val builder = AlertDialog.Builder(this)
 			builder.setTitle(""Android with kotlin says"")
 			builder.setMessage(""This is a alert dialog"")
 			builder.setPositiveButton(""Yes"",{ dialogInterface: DialogInterface, i: Int ->
 			Toast.makeText(this@MainActivity,""You Pressed Yes"",Toast.LENGTH_SHORT).show() 		})


			builder.setNegativeButton(""No"",{ dialogInterface: DialogInterface, i: Int ->
 			Toast.makeText(this@MainActivity,""You Pressed No"",Toast.LENGTH_SHORT).show()
 			})
 			builder.show()

 		}

	}


}

