	import android.os.Bundle
 import android.view.View
 import android.widget.AdapterView
 import android.widget.ArrayAdapter
 import android.widget.Toast
 import androidx.appcompat.app.AppCompatActivity
 import kotlinx.android.synthetic.main.activity_main.*


class MainActivity : AppCompatActivity()
 {
 	override fun onCreate(savedInstanceState: Bundle?)
 	{
 		val languages = resources.getStringArray(R.array.Languages)


		super.onCreate(savedInstanceState)
 		setContentView(R.layout.activity_main)

		if (spinner != null) {

 			val adapter = ArrayAdapter(this, android.R.layout.simple_spinner_item, languages)
 			spinner.adapter = adapter


		spinner.onItemSelectedListener = object :
 			AdapterView.OnItemSelectedListener {
 			override fun onItemSelected(parent: AdapterView<*>, view: View, position: Int, id: Long) {
 				Toast.makeText(this@MainActivity, getString(R.string.selected_item) + "" "" + """" + languages[position], Toast.LENGTH_SHORT).show()
 			}


			override fun onNothingSelected(parent: AdapterView<*>) {
 			// write code to perform some action
 			}

 		}


		}
 	}
 }

	import android.os.Bundle
 import androidx.appcompat.app.AppCompatActivity
 import kotlinx.android.synthetic.main.activity_main.*


class MainActivity : AppCompatActivity()
 {


	override fun onCreate(savedInstanceState: Bundle?)
 	{
 		super.onCreate(savedInstanceState)
 		setContentView(R.layout.activity_main)


		cb_button.setOnClickListener {


			if(cb1.isChecked())
 			{ 				Toast.makeText(activity,cb1.text,Toast.LENGTH_SHORT).show()
 			}


			if(cb2.isChecked())
 			{ 				Toast.makeText(activity,cb2.text,Toast.LENGTH_SHORT).show()
 			}


			if(cb3.isChecked())
 			{ 				Toast.makeText(activity,cb3.text,Toast.LENGTH_SHORT).show()
 			}


		}
 	}
 }

	import androidx.appcompat.app.AppCompatActivity
 import android.os.Bundle
 import android.widget.ArrayAdapter
 import android.widget.Toast
 import kotlinx.android.synthetic.main.activity_main.*


class MainActivity : AppCompatActivity() {


	override fun onCreate(savedInstanceState: Bundle?) {
 		super.onCreate(savedInstanceState)
 		setContentView(R.layout.activity_main)


		var arr:Array<String> = resources.getStringArray(R.array.list)
 		var arrayAdapter = ArrayAdapter<String>(this,android.R.layout.simple_expandable_list_item_1,arr)
 		lv.setAdapter(arrayAdapter)


		lv.setOnItemClickListener { parent, view, position, id ->
 			var str:String = parent.getItemAtPosition(position) as String
 			Toast.makeText(this,""Your in ${str}"",Toast.LENGTH_SHORT).show()
 		}

 	}
 }

	import android.os.Build
 import androidx.appcompat.app.AppCompatActivity
 import android.os.Bundle
 import android.webkit.WebViewClient
 import android.widget.Toast
 import androidx.annotation.RequiresApi
 import kotlinx.android.synthetic.main.activity_main.*
 import kotlinx.android.synthetic.main.custom_toast.*


class MainActivity : AppCompatActivity() {
 	@RequiresApi(Build.VERSION_CODES.O)
 	override fun onCreate(savedInstanceState: Bundle?) {
 		super.onCreate(savedInstanceState)
 		setContentView(R.layout.activity_main)


			webViewsetup()
 			Toast(this).apply {
 				duration = Toast.LENGTH_SHORT
 				view = layoutInflater.inflate(R.layout.custom_toast,toast)
 				show()
 			}

 	}


		@RequiresApi(Build.VERSION_CODES.O)
 		private fun webViewsetup() // makes sures the webpage opens in the app.
 		{
 			wv.webViewClient = WebViewClient()
 			wv.apply {
 				loadUrl(""https://www.google.com/"")
 				settings.javaScriptEnabled = true
 				settings.safeBrowsingEnabled = 

			}
 }
